'   end if;',
'   if shortcuts_rec.c002 = ''url'' then',
'       if shortcuts_rec.c015 = ''&''||''LOGOUT_URL.'' then',
'          apex_json.write(''url'', ''&LOGOUT_URL.'');',
'       else',
'        apex_json.write(''url'', shortcuts_rec.c015);',
'       end if;',
'   elsif shortcuts_rec.c002 = ''item'' then',
'     apex_json.write(''item_name'', shortcuts_rec.c007);',
'     if shortcuts_rec.c008 is not null then',
'        apex_json.write(''item_value'', shortcuts_rec.c008);',
