'     if shortcuts_rec.c013 is not null then',
'        apex_json.write(''IR_clearCache'', shortcuts_rec.c013);',
'     end if;',
'     if shortcuts_rec.c014 is not null then',
'        apex_json.write(''IR_value'', shortcuts_rec.c014);',
